    private void txt1KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt1KeyPressed
        if(evt.getKeyCode() == evt.VK_A) {
            txt2.setText("A pressionado");
        }
        if(evt.getKeyCode() == evt.VK_B) {
            txt2.setText("B pressionado");
        }
        if(evt.getKeyCode() == evt.VK_C) {
            txt2.setText("C pressionado");
        }
    }//GEN-LAST:event_txt1KeyPressed

    private void txt1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt1KeyReleased
        if(evt.getKeyCode() == evt.VK_A) {
            txt2.setText("A liberada");
        }
        if(evt.getKeyCode() == evt.VK_B) {
            txt2.setText("B liberada");
        }
        if(evt.getKeyCode() == evt.VK_C) {
            txt2.setText("C liberada");
        }
    }//GEN-LAST:event_txt1KeyReleased

    private void txt1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt1KeyTyped
        if(evt.getKeyChar() == 'a') {
            txt3.setText("a gerado");
        }
        if(evt.getKeyChar() == 'A') {
            txt3.setText("A gerado");
        }
        if(evt.getKeyChar() == 'b') {
            txt3.setText("b gerado");
        }
        if(evt.getKeyChar() == 'B') {
            txt3.setText("B gerado");
        }
        if(evt.getKeyChar() == 'c') {
            txt3.setText("c gerado");
        }
        if(evt.getKeyChar() == 'C') {
            txt3.setText("C gerado");
        }
    }//GEN-LAST:event_txt1KeyTyped

    private void jp1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jp1MouseClicked
        txt4.setText("X: " + evt.getX()+ "\nY: " + evt.getY()+ "\nBot√£o: " + evt.getButton());
    }//GEN-LAST:event_jp1MouseClicked

